import React, { useState } from 'react';
import moment from 'moment';
import { Form, Image, Input, List, Space, Button } from 'antd';
import {
    CalendarOutlined,
    HeatMapOutlined,
    MailOutlined,
    PhoneOutlined,
    UserOutlined,
    PlusCircleOutlined,
    EditOutlined,
    CloseCircleOutlined,
} from '@ant-design/icons';
import { TbHeart, TbTargetArrow } from 'react-icons/tb';
import { MdBusinessCenter, MdVerified } from 'react-icons/md';
import { toast } from 'react-toastify';

toast.configure({
    autoClose: 2000,
    draggable: false,
    limit: 3,
    closeOnClick: true,
});

const { TextArea } = Input;

function FormProfile(props) {
    const { data, employeeInfo, workExperiencesList, setWorkExperiencesList, profileInfo } = props;
    const employeeCv = data?.cv;
    const resume = data?.resume;

    const [workData, setWorkData] = useState(workExperiencesList);
    const [editingIndex, setEditingIndex] = useState(-1);

    const handleAdd = () => {
        console.log("==>", workData)
        const newData = [{ company: '', position: '', detail: '', startDate: '', endDate: '' }, ...workData];
        setWorkData(newData);
        setWorkExperiencesList(newData);
        setEditingIndex(0);
    };

    const handleDelete = (index) => {
        const newData = [...workData];
        newData.splice(index, 1);
        setWorkData(newData);
        setWorkExperiencesList(newData);
    };

    const handleEdit = (index) => {
        if (editingIndex > -1) {
            if (
                !workData[editingIndex].company ||
                !workData[editingIndex].position ||
                !workData[editingIndex].detail ||
                !workData[editingIndex].startDate ||
                !workData[editingIndex].endDate
            ) {
                toast.warning('Nhập đủ thông tin');
                return;
            }
        }
        setEditingIndex(index);
    };

    const handleInputChange = (e, index) => {
        const { name, value } = e.target;
        const newData = [...workData];
        newData[index][name] = value;
        setWorkData(newData);
        setWorkExperiencesList(newData);
    };

    const handleSave = (index) => {
        if (
            !workData[editingIndex].company ||
            !workData[editingIndex].position ||
            !workData[editingIndex].detail ||
            !workData[editingIndex].startDate ||
            !workData[editingIndex].endDate
        ) {
            toast.warning('Nhập đủ thông tin');
            return;
        }
        setEditingIndex(-1);
    };

    const teamId = () => {
        if (employeeInfo?.teamId === 1) {
            return 'Back-end';
        } else if (employeeInfo?.teamId === 2) {
            return 'Front-end';
        } else if (employeeInfo?.teamId === 3) {
            return 'Tester';
        } else {
            return;
        }
    };
    return (
        <div className="profile">
            <div className="profile-left">
                <div className="profile-left-info">
                    <Image
                        className="profile-img"
                        src={employeeInfo?.photoUrl ? employeeInfo?.photoUrl : 'error'}
                        fallback="data:image/png;base64,
                                "
                    />

                    <h3>{employeeInfo?.fullName || resume?.fullName}</h3>
                    <p>{teamId()}</p>
                </div>

                <div className="profile-left-bottom">
                    <p>
                        <CalendarOutlined />{' '}
                        {moment(employeeInfo?.dateOfBirth || resume?.dateOfBirth).format('DD/MM/YYYY')}
                    </p>

                    <p>
                        <UserOutlined /> {employeeInfo?.gender === 0 || resume?.gender === 0 ? 'Nam' : 'Nữ'}
                    </p>

                    <p>
                        <PhoneOutlined /> {employeeInfo?.phone || resume?.phone}
                    </p>

                    <p>
                        <MailOutlined /> {employeeInfo?.email || resume?.email}
                    </p>

                    <p>
                        <HeatMapOutlined /> {employeeInfo?.address || resume?.address}
                    </p>
                </div>
            </div>
            {profileInfo ? (
                <div className="profile-right curriculum_vitae">
                    <div className="profile-right-general">
                        <div className="profile-right-title">
                            <div className="icon-profile">
                                <TbTargetArrow />
                            </div>
                            <h4>Mục tiêu</h4>
                        </div>
                        <Form.Item name="careerGoal">
                            <TextArea
                                autoSize={{
                                    minRows: 1,
                                    maxRows: 3,
                                }}
                            />
                        </Form.Item>
                    </div>

                    <div className="profile-right-general">
                        <div className="profile-right-title">
                            <div className="icon-profile">
                                <MdVerified />
                            </div>
                            <h4>Các kỹ năng</h4>
                        </div>
                        <p>
                            <Form.Item name="skill">
                                <TextArea
                                    autoSize={{
                                        minRows: 1,
                                        maxRows: 3,
                                    }}
                                />
                            </Form.Item>
                        </p>
                    </div>

                    <div className="profile-right-general">
                        <div className="profile-right-title">
                            <div className="icon-profile">
                                <TbHeart />
                            </div>
                            <h4>Sở thích</h4>
                        </div>
                        <p>
                            <Form.Item name="hobby">
                                <TextArea
                                    autoSize={{
                                        minRows: 1,
                                        maxRows: 3,
                                    }}
                                />
                            </Form.Item>
                        </p>
                    </div>

                    <div className="profile-right-general flex j-between">
                        <div className="profile-right-title">
                            <div className="icon-profile">
                                <MdBusinessCenter />
                            </div>
                            <h4>Kinh nghiệm làm việc</h4>
                        </div>

                        <div>
                            <button type="button" className="btn-hiden" onClick={handleAdd}>
                                <PlusCircleOutlined />
                            </button>
                        </div>
                    </div>

                    <div className="list-work-experiences">
                        <List
                            bordered
                            dataSource={workData}
                            renderItem={(item, index) => (
                                <List.Item>
                                    {editingIndex === index ? (
                                        <Space>
                                            <div className="profile-form">
                                                <div className="list-work-info">
                                                    <div>
                                                        <Form.Item label="Công ty: ">
                                                            <Input
                                                                name="company"
                                                                value={item.company}
                                                                onChange={(e) => handleInputChange(e, index)}
                                                            />
                                                        </Form.Item>
                                                    </div>
                                                    <div>
                                                        <Form.Item label="Vị trí: ">
                                                            <Input
                                                                name="position"
                                                                value={item.position}
                                                                onChange={(e) => handleInputChange(e, index)}
                                                            />
                                                        </Form.Item>
                                                    </div>
                                                    <div>
                                                        <Form.Item label="Mô tả: ">
                                                            <TextArea
                                                                name="detail"
                                                                autoSize={{
                                                                    minRows: 1,
                                                                    maxRows: 2,
                                                                }}
                                                                value={item.detail}
                                                                onChange={(e) => handleInputChange(e, index)}
                                                            />
                                                        </Form.Item>
                                                    </div>
                                                    <div className="profile-date">
                                                        <Form.Item label="Ngày bắt đầu">
                                                            <Input
                                                                name="startDate"
                                                                value={item.startDate}
                                                                type="date"
                                                                onChange={(e) => handleInputChange(e, index)}
                                                            />
                                                        </Form.Item>
                                                        <Form.Item label="Ngày kết thúc">
                                                            <Input
                                                                name="endDate"
                                                                value={item.endDate}
                                                                type="date"
                                                                onChange={(e) => handleInputChange(e, index)}
                                                            />
                                                        </Form.Item>
                                                    </div>
                                                </div>

                                                <div className="list-work-btn">
                                                    <Button onClick={() => handleSave(index)}>Save</Button>
                                                </div>
                                            </div>
                                        </Space>
                                    ) : (
                                        <Space>
                                            <div>
                                                <div>
                                                    <div className="list-work-show">
                                                        <p>
                                                            Công ty: <span>{item?.company}</span>{' '}
                                                        </p>
                                                        <div>
                                                            <span>
                                                                {moment(item?.startDate, 'YYYY-MM-DD').format(
                                                                    'DD-MM-YYYY',
                                                                )}{' '}
                                                                -{' '}
                                                            </span>
                                                            <span>
                                                                {moment(item?.endDate, 'YYYY-MM-DD').format(
                                                                    'DD-MM-YYYY',
                                                                )}
                                                            </span>
                                                        </div>
                                                    </div>
                                                    <p>
                                                        Vị trí: <span>{item?.position}</span>
                                                    </p>
                                                    <p>
                                                        Mô tả: <span>{item?.detail}</span>
                                                    </p>
                                                </div>
                                                <div className="list-work-show-btn">
                                                    <Button onClick={() => handleEdit(index)}>
                                                        <EditOutlined />
                                                    </Button>
                                                    <Button onClick={() => handleDelete(index)}>
                                                        <CloseCircleOutlined />
                                                    </Button>
                                                </div>
                                            </div>
                                        </Space>
                                    )}
                                </List.Item>
                            )}
                        />
                    </div>
                </div>
            ) : (
                <div className="profile-right">
                    <div className="profile-right-general">
                        <div className="profile-right-title">
                            <div className="icon-profile">
                                <TbTargetArrow />
                            </div>
                            <h4>Mục tiêu</h4>
                        </div>
                        <p>{employeeCv?.careerGoal}</p>
                    </div>

                    <div className="profile-right-general">
                        <div className="profile-right-title">
                            <div className="icon-profile">
                                <MdVerified />
                            </div>
                            <h4>Các kỹ năng</h4>
                        </div>
                        <p>{employeeCv?.skill}</p>
                    </div>

                    <div className="profile-right-general">
                        <div className="profile-right-title">
                            <div className="icon-profile">
                                <TbHeart />
                            </div>
                            <h4>Sở thích</h4>
                        </div>
                        <p>{employeeCv?.hobby}</p>
                    </div>

                    <div className="profile-right-general">
                        <div className="profile-right-title">
                            <div className="icon-profile">
                                <MdBusinessCenter />
                            </div>
                            <h4>Kinh nghiệm làm việc</h4>
                        </div>

                        <div>
                            {employeeCv?.workExperiences.map((item, index) => (
                                <div key={index} className="profile-right-experience">
                                    <div className="profile-right-experience-title">
                                        <p>{item.company}</p>
                                        <p>
                                            <span>{moment(item?.startDate).format('DD/MM/YYYY')} - </span>
                                            <span>{moment(item?.endDate).format('DD/MM/YYYY')}</span>
                                        </p>
                                    </div>
                                    <p className="position">{item.position}</p>
                                    <p className="detail">{item.detail}</p>
                                </div>
                            ))}
                        </div>
                    </div>
                </div>
            )}
        </div>
    );
}

export default FormProfile;
